#!/usr/bin/env node
import process from 'node:process';
import express from 'express';
import { fetch } from 'undici';
import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { SSEServerTransport } from '@modelcontextprotocol/sdk/server/sse.js';
import { ListToolsRequestSchema, CallToolRequestSchema } from '@modelcontextprotocol/sdk/types.js';

// Proxy target: your ai-search backend
const BASE_URL = process.env.AI_SEARCH_BASE_URL || 'http://localhost:8081';
// Mode: 'stdio' (default, for VS Code Copilot) or 'http' (SSE HTTP server)
const MODE = (process.env.MCP_MODE || 'stdio').toLowerCase();
// HTTP SSE settings (for MODE=http)
const PORT = Number(process.env.MCP_HTTP_PORT || 8091);
const SSE_PATH = process.env.MCP_SSE_PATH || '/mcp/sse';
const POST_PATH = process.env.MCP_POST_PATH || '/mcp/messages';

async function doFetch(path, opts = {}) {
  const url = path.startsWith('http') ? path : `${BASE_URL}${path}`;
  const res = await fetch(url, {
    ...opts,
    headers: {
      'content-type': 'application/json',
      ...(opts.headers || {}),
    },
  });
  if (!res.ok) {
    const text = await res.text().catch(() => '');
    throw new Error(`HTTP ${res.status} ${res.statusText}: ${text?.slice(0, 500)}`);
  }
  const ct = res.headers.get('content-type') || '';
  if (ct.includes('application/json')) return res.json();
  return res.text();
}

function buildServer() {
  const server = new Server({ name: 'ai-search', version: '0.4.0' }, { capabilities: { tools: {} } });
  server.setRequestHandler(ListToolsRequestSchema, async () => ({
    tools: [
      {
        name: 'querySearchIndex',
        description: 'Query the Azure AI Search index for cardiology content. Returns normalized hits.',
        input_schema: {
          type: 'object', additionalProperties: false,
          properties: {
            query: { type: 'string' },
            top: { type: 'number' },
            filters: { type: 'string' },
            select: { type: 'array', items: { type: 'string' } },
          }, required: ['query']
        },
      },
      {
        name: 'analyzeText',
        description: 'Analyze a clinical note. Returns Assessment, Plan, citations, and meta.',
        input_schema: {
          type: 'object', additionalProperties: false,
          properties: { note: { type: 'string' } }, required: ['note']
        },
      },
      {
        name: 'parseAzureNote',
        description: 'Summarize and parse a cardiology note stored in Azure Blob Storage using GPT refinement.',
        input_schema: {
          type: 'object', additionalProperties: false,
          properties: { container: { type: 'string' }, blob: { type: 'string' } },
          required: ['container', 'blob']
        },
      },
    ],
  }));

  server.setRequestHandler(CallToolRequestSchema, async (req) => {
    const name = req.params?.name;
    const args = /** @type {any} */ (req.params?.arguments) || {};

    if (name === 'querySearchIndex') {
      const q = String(args.query || '').trim();
      if (!q) return { content: [{ type: 'text', text: 'Error: query is required' }] };
      const top = Number.isFinite(args.top) ? Math.min(50, Math.max(1, Number(args.top))) : 10;
      const filters = args.filters ? String(args.filters) : undefined;
      const select = Array.isArray(args.select) ? args.select.filter(s => typeof s === 'string') : undefined;
      const body = { q, top, skip: 0, select, filters };
      const json = await doFetch('/search', { method: 'POST', body: JSON.stringify(body) });
      const summary = { ok: !!json?.ok, count: json?.count ?? (json?.results?.length || 0), fields: select };
      return { content: [ { type: 'text', text: `querySearchIndex results for "${q}" (top ${top}): ${summary.count}` }, { type: 'text', text: JSON.stringify(json, null, 2) } ] };
    }

    if (name === 'analyzeText') {
      const note = String(args.note || '').trim();
      if (!note) return { content: [{ type: 'text', text: 'Error: note is required' }] };
      const json = await doFetch('/api/analyze-note', { method: 'POST', body: JSON.stringify({ note }) });
      return { content: [ { type: 'text', text: 'Assessment and Plan generated by ai-search backend:' }, { type: 'text', text: JSON.stringify(json, null, 2) } ] };
    }

    if (name === 'parseAzureNote') {
      const container = String(args.container || '').trim();
      const blob = String(args.blob || '').trim();
      if (!container || !blob) return { content: [{ type: 'text', text: 'Error: container and blob are required' }] };
      const json = await doFetch('/parse', { method: 'POST', body: JSON.stringify({ container, blob }) });
      return { content: [ { type: 'text', text: `parseAzureNote for ${container}/${blob}` }, { type: 'text', text: JSON.stringify(json, null, 2) } ] };
    }

    return { content: [{ type: 'text', text: `Unknown tool: ${name}` }] };
  });

  return server;
}

async function main() {
  if (MODE === 'http') {
    const app = express();
    app.use(express.json());
    /** @type {Map<string, import('@modelcontextprotocol/sdk/dist/server/sse.js').SSEServerTransport>} */
    const sessions = new Map();

    app.get('/mcp/health', (_req, res) => res.json({ ok: true, mode: 'sse', post: POST_PATH, sse: SSE_PATH }));

    app.get(SSE_PATH, async (_req, res) => {
      try {
        const transport = new SSEServerTransport(POST_PATH, res);
        const server = buildServer();
        await server.connect(transport);
        sessions.set(transport.sessionId, transport);
      } catch (e) {
        res.status(500).type('text/plain').send(String(e?.message || e));
      }
    });

    app.post(POST_PATH, async (req, res) => {
      const sessionId = String(req.query.sessionId || '').trim();
      const transport = sessionId ? sessions.get(sessionId) : undefined;
      if (!transport) return res.status(404).type('text/plain').send('Unknown or missing session');
      await transport.handlePostMessage(req, res);
    });

    app.listen(PORT, () => {
      console.log(`[mcp] HTTP SSE server listening on http://localhost:${PORT}${SSE_PATH}`);
      console.log(`[mcp] Proxying backend at ${BASE_URL}`);
    });
  } else {
    // stdio mode for Copilot Chat
    const server = buildServer();
    const transport = new StdioServerTransport();
    await server.connect(transport);
    // Process will stay alive awaiting stdio from Copilot
  }
}

main().catch(err => {
  console.error('[mcp] Fatal error:', err?.stack || err?.message || String(err));
  process.exit(1);
});

import { execSync } from "node:child_process";
import { promises as fs } from "node:fs";
import path from "node:path";
import { fileURLToPath } from "node:url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const rootDir = path.resolve(__dirname, "..", "..");
const reportsDir = path.join(rootDir, "reports", "cleanup");

function safeExec(command) {
  try {
    return execSync(command, { cwd: rootDir, encoding: "utf8" }).trim();
  } catch (error) {
    return `error: ${error.message}`;
  }
}

const baseline = {
  generatedAt: new Date().toISOString(),
  branch: safeExec("git rev-parse --abbrev-ref HEAD"),
  commit: safeExec("git rev-parse HEAD"),
  status: safeExec("git status -sb"),
  nodeVersion: process.version,
  npmVersion: safeExec("npm --version"),
  platform: {
    platform: process.platform,
    arch: process.arch,
    release: safeExec("uname -a"),
  },
  env: {
    NODE_ENV: process.env.NODE_ENV ?? "",
    CI: process.env.CI ?? "",
  },
};

await fs.mkdir(reportsDir, { recursive: true });
const jsonPath = path.join(reportsDir, "baseline.json");
const txtPath = path.join(reportsDir, "baseline.txt");

await fs.writeFile(jsonPath, JSON.stringify(baseline, null, 2));

const txtLines = [
  `Generated: ${baseline.generatedAt}`,
  `Branch: ${baseline.branch}`,
  `Commit: ${baseline.commit}`,
  "",
  "Git Status:",
  baseline.status,
  "",
  `Node: ${baseline.nodeVersion}`,
  `npm: ${baseline.npmVersion}`,
  `Platform: ${baseline.platform.release}`,
];

await fs.writeFile(txtPath, txtLines.join("\n"));

console.log(`Baseline written to ${path.relative(rootDir, jsonPath)}`);

function safeRequire(moduleId) {
  try {
    // eslint-disable-next-line global-require
    return require(moduleId).default;
  } catch (error) {
    return () => ({});
  }
}

module.exports = {
  ignoreMatches: [
    "@playwright/test",
    "@size-limit/*",
    "tailwindcss",
    "vite",
    "ts-node",
    "typescript",
    "eslint",
    "eslint-plugin-*",
    "@typescript-eslint/*",
    "@modelcontextprotocol/*",
  ],
  ignoreDirs: ["dist", "reports", "docs", "tests/fixtures"],
  specials: [
    safeRequire("depcheck/dist/special/eslint"),
    safeRequire("depcheck/dist/special/babel"),
    safeRequire("depcheck/dist/special/bin"),
    safeRequire("depcheck/dist/special/webpack"),
  ],
};

import { promises as fs } from "node:fs";
import path from "node:path";
import { fileURLToPath } from "node:url";
import { findUnusedExports } from "./export-graph.mjs";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const rootDir = path.resolve(__dirname, "..", "..");
const reportsDir = path.join(rootDir, "reports", "cleanup");
const allowlistPath = path.join(__dirname, "allowlist.json");

async function loadAllowlist() {
  try {
    const content = await fs.readFile(allowlistPath, "utf8");
    return JSON.parse(content);
  } catch {
    return { modules: [], exports: [], assets: [] };
  }
}

const allowlist = await loadAllowlist();

const results = await findUnusedExports(allowlist);

await fs.mkdir(reportsDir, { recursive: true });
const outputPath = path.join(reportsDir, "dead-exports.json");
await fs.writeFile(outputPath, JSON.stringify({ unused: results }, null, 2));
console.log(`Dead export report written to ${path.relative(rootDir, outputPath)}`);

import { createHash } from "node:crypto";
import { promises as fs } from "node:fs";
import path from "node:path";
import { fileURLToPath } from "node:url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const rootDir = path.resolve(__dirname, "..", "..");
const reportsDir = path.join(rootDir, "reports", "cleanup");
const allowlistPath = path.join(__dirname, "allowlist.json");

async function loadAllowlist() {
  try {
    const content = await fs.readFile(allowlistPath, "utf8");
    return JSON.parse(content);
  } catch {
    return { modules: [], exports: [], assets: [] };
  }
}

const allowlist = await loadAllowlist();

const assetRoots = ["public", "assets", "styles", "static"].map((dir) =>
  path.join(rootDir, dir),
);

async function walk(directory) {
  const entries = await fs.readdir(directory, { withFileTypes: true });
  const files = [];
  for (const entry of entries) {
    const entryPath = path.join(directory, entry.name);
    if (entry.isDirectory()) {
      files.push(...(await walk(entryPath)));
    } else if (entry.isFile()) {
      files.push(entryPath);
    }
  }
  return files;
}

async function collectAssets() {
  const assets = [];
  for (const root of assetRoots) {
    try {
      const stats = await fs.stat(root);
      if (!stats.isDirectory()) {
        continue;
      }
    } catch {
      continue;
    }

    const files = await walk(root);
    for (const filePath of files) {
      const relPath = path.relative(rootDir, filePath).replace(/\\/g, "/");
      if (allowlist.assets?.includes(relPath)) {
        continue;
      }

      const ext = path.extname(filePath).toLowerCase();
      const excluded = [
        ".map",
        ".txt",
        ".md",
        ".html",
        ".json",
        ".js",
      ];
      if (ext === ".json" && relPath.startsWith("public/")) {
        // allowlist handled separately
      } else if (excluded.includes(ext)) {
        continue;
      }

      const buffer = await fs.readFile(filePath);
      const hash = createHash("sha1").update(buffer).digest("hex");
      assets.push({
        path: relPath,
        size: buffer.length,
        hash,
        extension: ext,
      });
    }
  }
  return assets;
}

const assetRecords = await collectAssets();

const searchRoots = [
  "src",
  "scripts",
  "services",
  "pages",
  "styles",
  "tests",
  "index.html",
  "README.md",
];

async function readIfExists(relPath) {
  const targetPath = path.join(rootDir, relPath);
  try {
    const stats = await fs.stat(targetPath);
    if (stats.isDirectory()) {
      const files = await walk(targetPath);
      const contents = await Promise.all(
        files.map(async (file) => ({
          path: path.relative(rootDir, file).replace(/\\/g, "/"),
          content: await fs.readFile(file, "utf8"),
        })),
      );
      return contents;
    }
    return [
      {
        path: relPath,
        content: await fs.readFile(targetPath, "utf8"),
      },
    ];
  } catch {
    return [];
  }
}

const searchFiles = (
  await Promise.all(searchRoots.map((rel) => readIfExists(rel)))
).flat();

const usageMap = new Map(assetRecords.map((asset) => [asset.path, { used: false }]));

for (const file of searchFiles) {
  const content = file.content;
  for (const asset of assetRecords) {
    if (usageMap.get(asset.path)?.used) {
      continue;
    }
    const fileName = path.basename(asset.path);
    if (
      content.includes(asset.path) ||
      content.includes(asset.path.replace(/^public\//, "")) ||
      content.includes(fileName)
    ) {
      usageMap.get(asset.path).used = true;
    }
  }
}

const unusedAssets = assetRecords.filter((asset) => !usageMap.get(asset.path).used);

await fs.mkdir(reportsDir, { recursive: true });
const outputPath = path.join(reportsDir, "assets.json");
await fs.writeFile(
  outputPath,
  JSON.stringify({
    scanned: assetRecords.length,
    unused: unusedAssets,
  }, null, 2),
);

console.log(
  `Asset analysis complete. ${unusedAssets.length} unused assets recorded at ${path.relative(
    rootDir,
    outputPath,
  )}`,
);

import { promises as fs } from "node:fs";
import path from "node:path";
import { fileURLToPath } from "node:url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const rootDir = path.resolve(__dirname, "..", "..");
const reportsDir = path.join(rootDir, "reports", "cleanup");
const allowlistPath = path.join(__dirname, "allowlist.json");

const isDryRun = process.argv.includes("--dry");

async function loadAllowlist() {
  try {
    const content = await fs.readFile(allowlistPath, "utf8");
    return JSON.parse(content);
  } catch {
    return { modules: [], exports: [], assets: [] };
  }
}

const allowlist = await loadAllowlist();

async function readJson(relPath) {
  try {
    const filePath = path.join(reportsDir, relPath);
    const content = await fs.readFile(filePath, "utf8");
    return JSON.parse(content);
  } catch {
    return null;
  }
}

async function readText(relPath) {
  try {
    const filePath = path.join(reportsDir, relPath);
    const content = await fs.readFile(filePath, "utf8");
    return content;
  } catch {
    return "";
  }
}

function recordVote(votes, filePath, tool, detail) {
  const normalized = filePath.replace(/\\/g, "/");
  const entry = votes.get(normalized) ?? {
    file: normalized,
    tools: new Set(),
    details: [],
  };
  entry.tools.add(tool);
  if (detail) {
    entry.details.push({ tool, detail });
  }
  votes.set(normalized, entry);
}

const votes = new Map();

const tsPruneRaw = await readText("tsprune.txt");
if (tsPruneRaw) {
  for (const line of tsPruneRaw.split(/\r?\n/)) {
    if (!line.trim()) continue;
    const [filePart, ...rest] = line.split(":");
    if (!filePart) continue;
    const relPath = filePart.trim();
    recordVote(votes, relPath, "ts-prune", rest.join(":").trim());
  }
}

const eslintReport = await readJson("eslint.json");
if (Array.isArray(eslintReport)) {
  for (const fileResult of eslintReport) {
    const relPath = path.relative(rootDir, fileResult.filePath || "");
    if (!relPath || relPath.startsWith("..")) continue;
    const relevantMessages = (fileResult.messages || []).filter((message) =>
      [
        "no-unused-vars",
        "@typescript-eslint/no-unused-vars",
        "sonarjs/no-dead-store",
        "no-dead-code",
      ].includes(message.ruleId),
    );
    if (!relevantMessages.length) continue;
    const detail = relevantMessages
      .map((msg) => `${msg.ruleId}@${msg.line}:${msg.column}`)
      .join(", ");
    recordVote(votes, relPath, "eslint", detail);
  }
}

const deadExports = await readJson("dead-exports.json");
if (deadExports?.unused) {
  for (const item of deadExports.unused) {
    if (!item?.file) continue;
    recordVote(votes, item.file, "ts-morph", item.export);
  }
}

const assets = await readJson("assets.json");
if (assets?.unused) {
  for (const asset of assets.unused) {
    if (!asset?.path) continue;
    recordVote(votes, asset.path, "asset-scan", asset.hash);
  }
}

function isConfigFile(filePath) {
  const configNames = [
    "tsconfig.json",
    "vite.config.js",
    "webpack.config.js",
    "jest.config.js",
    "eslint.config.js",
    "eslint.config.cjs",
    "tailwind.config.js",
