/**
 * REST API fallback for Azure Search when SDK returns empty results
 * @param {string} endpoint - Azure Search endpoint URL
 * @param {string} indexName - Index name
 * @param {string} apiKey - Query API key
 * @param {string} query - Search query
 * @param {any} options - Search options
 */
export async function restSearch(endpoint, indexName, apiKey, query, options = {}) {
  const { top = 10, skip = 0, select, orderby, filters, semantic, includeTotalCount = true } = options;

  const apiVersion = "2024-07-01";
  const url = `${endpoint}/indexes/${indexName}/docs/search?api-version=${apiVersion}`;

  /** @type {any} */
  const body = {
    search: query || "*",
    top,
    skip,
    count: includeTotalCount,
  };

  if (select && Array.isArray(select) && select.length) {
    body.select = select.join(",");
  }

  if (orderby) {
    body.orderby = orderby;
  }

  if (filters) {
    body.filter = filters;
  }

  // Semantic search configuration
  if (semantic) {
    body.queryType = "semantic";
    body.semanticConfiguration = semantic;
    body.captions = "extractive";
    body.answers = "extractive|count-3";
  }

  const response = await fetch(url, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "api-key": apiKey,
    },
    body: JSON.stringify(body),
  });

  if (!response.ok) {
    const text = await response.text();
    const err = new Error(`REST search failed ${response.status}: ${text}`);
    // @ts-ignore augment error with status for retry filter
    err.status = response.status;
    throw err;
  }

  const json = await response.json();
  return json;
}

/**
 * Check if an object has any enumerable properties
 * @param {any} obj
 */
export function hasEnumerableProps(obj) {
  if (!obj || typeof obj !== "object") return false;
  return Object.keys(obj).length > 0;
}

/* eslint-env node */

/**
 * Exponential backoff with optional jitter and retry filter.
 * @template T
 * @param {() => Promise<T>} fn
 * @param {{
 *   retries?: number,
 *   baseMs?: number,
 *   maxMs?: number,
 *   jitter?: boolean,
 *   retryOn?: (err: any) => boolean
 * }} [opts]
 * @returns {Promise<T>}
 */
export async function withBackoff(fn, opts = {}) {
  const {
    retries = 4,
    baseMs = 200,
    maxMs = 4000,
    jitter = true,
    retryOn = (err) => {
      const s = err?.status ?? err?.statusCode ?? err?.response?.status;
      return s === 429 || (s >= 500 && s < 600);
    },
  } = opts;

  let attempt = 0;
  /** @type {any} */
  let lastErr;
  while (attempt <= retries) {
    try {
      return await fn();
    } catch (e) {
      lastErr = e;
      if (!retryOn(e) || attempt === retries) throw e;
      const delay = Math.min(maxMs, baseMs * 2 ** attempt) * (jitter ? 0.75 + Math.random() * 0.5 : 1);
      await new Promise((r) => setTimeout(r, delay));
      attempt++;
    }
  }
  throw lastErr;
}

// helpers/search-normalize.js
// JS version of the provided TypeScript snippet

function extractDoc(hit) {
  if (hit && typeof hit.document === "object") return hit.document;
  if (hit && typeof hit._source === "object") return hit._source;
  if (hit && typeof hit.documentFields === "object") return hit.documentFields;
  const { ["@search.score"]: _ignored, ...rest } = hit || {};
  return rest && typeof rest === "object" ? rest : {};
}

function extractScore(hit) {
  if (hit && typeof hit.score === "number") return hit.score;
  if (hit && typeof hit._score === "number") return hit._score;
  if (hit && typeof hit["@search.score"] === "number") return hit["@search.score"];
  return undefined;
}

function extractId(doc) {
  return doc?.id ?? doc?.key ?? doc?.documentId ?? doc?.["@search.documentId"] ?? doc?._id;
}

export function normalizeHit(hit) {
  const doc = extractDoc(hit);
  const score = extractScore(hit);
  const id = extractId(doc);
  return { id, score, ...doc };
}

export function normalizeResultContainer(r) {
  if (Array.isArray(r)) return r;
  if (Array.isArray(r?.results)) return r.results;
  if (Array.isArray(r?.value)) return r.value;
  if (Array.isArray(r?.hits)) return r.hits;
  return [];
}

